mapper.select:
select sum(total_lesson) total,sum(used) used,sum(rem) rem
from member_course
where mem_id= #{memId,jdbcType=VARCHAR}

select sum(total_lesson) total,sum(used) used,sum(rem) rem
from member_course
where mem_id= #{memId,jdbcType=VARCHAR}
and course_id in (select course_id from course
where 1=1

  and coach_id=#{coachId,jdbcType=VARCHAR}


  and club_id=#{clubId,jdbcType=VARCHAR}

)
