mapper.select:
    select max(seq_no)
    from member_lesson
    where mem_Id = #{memId,jdbcType=VARCHAR}
    and sale_id=#{saleId,jdbcType=VARCHAR}
--     where id like CONCAT('%',date_format(now(), '%Y%m%d'),'%')

select *
from member_lesson
where mem_Id = #{memId,jdbcType=VARCHAR}
and status=#{status,jdbcType=TINYINT}

select *
from v_member_lesson
where mem_Id = #{memId,jdbcType=VARCHAR}
and status=#{status,jdbcType=TINYINT}

select






            start_time_1,end_time_1 from coach_lesson where course_id=#{course_id,jdbcType=VARCHAR}
            and club_id=#{club_id,jdbcType=VARCHAR}
            and coach_id=#{coach_id,jdbcType=VARCHAR}


            b.coach_id,b.name from coach_lesson a,coach b where a.course_id=#{course_id,jdbcType=VARCHAR}
            and a.club_id=#{club_id,jdbcType=VARCHAR}
            and a.coach_id=b.coach_id




        b.club_id,b.name from coach_lesson a,club b where a.course_id=#{course_id,jdbcType=VARCHAR}
        and a.club_id=b.club_id




    distinct sale_id from member_lesson where mem_id=#{mem_id,jdbcType=VARCHAR} and status=0

select *
from v_member_lesson
where
club_id = #{clubId,jdbcType=VARCHAR}

    and status = #{status,jdbcType=TINYINT}

order by start_time_1 desc

select * from v_member_lesson where club_id = #{clubId,jdbcType=VARCHAR}
and status = #{status,jdbcType=TINYINT}
and start_time_1 like CONCAT('%',#{reg_date,jdbcType=VARCHAR},'%')
order by start_time_1 desc

mapper.update:
update member_lesson


    real_club = #{realClub,jdbcType=VARCHAR},


    real_coach = #{realCoach,jdbcType=VARCHAR},


    start_time_1 = #{startTime1,jdbcType=TIMESTAMP},


    end_time_1 = #{endTime1,jdbcType=TIMESTAMP},


    bz1 = #{bz1,jdbcType=VARCHAR},


    where mem_Id = #{memId,jdbcType=VARCHAR}
    and sale_id=#{saleId,jdbcType=VARCHAR}
    and seq_no=#{seqNo,jdbcType=INTEGER}
    and kc_id=#{kcId,jdbcType=VARCHAR}

update member_lesson set

  start_time_2 = #{startTime2,jdbcType=TIMESTAMP},


  end_time_2 = #{endTime2,jdbcType=TIMESTAMP},

status=#{status,jdbcType=TINYINT}
where kc_id=#{kcId,jdbcType=VARCHAR} and seq_no=#{seqNo,jdbcType=INTEGER}
