mapper.select:
select
course_id,coach_id,sum(kt_cnt) kt_sum,sum(xt_cnt) xt_sum
from t_income_dtl
where 1=1

  and course_id=#{courseId,jdbcType=VARCHAR}


and coach_id=#{coachId,jdbcType=VARCHAR}

and reg_date =#{regDate,jdbcType=VARCHAR}
group by course_id,coach_id

select
course_id,club_id,sum(kt_cnt) kt_sum
from t_income_dtl
where 1=1

  and course_id=#{courseId,jdbcType=VARCHAR}


  and club_id=#{clubId,jdbcType=VARCHAR}

and reg_date =#{regDate,jdbcType=VARCHAR}
group by course_id,club_id

mapper.insert:
insert into t_income_dtl (course_id, coach_id, club_id, 
  reg_date, KT_CNT, XT_CNT, 
  bz1, bz2, reg_time
  )
values (#{courseId,jdbcType=VARCHAR}, #{coachId,jdbcType=VARCHAR}, #{clubId,jdbcType=VARCHAR}, 
  #{regDate,jdbcType=DATE}, #{ktCnt,jdbcType=DECIMAL}, #{xtCnt,jdbcType=DECIMAL}, 
  #{bz1,jdbcType=VARCHAR}, #{bz2,jdbcType=VARCHAR}, #{regTime,jdbcType=TIMESTAMP}
  )
