mapper.select:
select user_id,sum(kt_sum) kt_sum,sum(xt_sum) xt_sum from t_income where 1=1
and user_id=#{userId,jdbcType=VARCHAR}
and DATE_FORMAT(reg_date,'%Y-%m') = DATE_FORMAT(#{regDate,jdbcType=VARCHAR},'%Y-%m') group by user_id

mapper.insert:
insert into t_income (user_id, user_type, reg_date, 
  KT_PER, KT_SUM, KT_CNT, 
  XT_PER, XT_SUM, XT_CNT, 
  bz1, bz2, reg_time
  )
values (#{userId,jdbcType=VARCHAR}, #{userType,jdbcType=VARCHAR}, #{regDate,jdbcType=DATE}, 
  #{ktPer,jdbcType=DECIMAL}, #{ktSum,jdbcType=DECIMAL}, #{ktCnt,jdbcType=DECIMAL}, 
  #{xtPer,jdbcType=DECIMAL}, #{xtSum,jdbcType=DECIMAL}, #{xtCnt,jdbcType=DECIMAL}, 
  #{bz1,jdbcType=VARCHAR}, #{bz2,jdbcType=VARCHAR}, #{regTime,jdbcType=TIMESTAMP}
  )
