mapper.select:
select 

from t_member_lesson_cancel
where kc_id = #{kcId,jdbcType=VARCHAR}
  and seq_no = #{seqNo,jdbcType=TINYINT}

mapper.delete:
delete from t_member_lesson_cancel
where kc_id = #{kcId,jdbcType=VARCHAR}
  and seq_no = #{seqNo,jdbcType=TINYINT}

mapper.select:
select

from t_member_lesson_cancel
where 1=1

  and mem_Id = #{memId,jdbcType=VARCHAR}


  and status = #{status,jdbcType=TINYINT}


  and coach_id = #{coachId,jdbcType=VARCHAR}


  and club_id = #{clubId,jdbcType=VARCHAR}


  and start_time_1 like CONCAT('%',#{reg_date,jdbcType=VARCHAR},'%')

order by kc_id,seq_no,start_time_1 desc
